*,:after,:before{position:relative;box-sizing:border-box}html{margin:0;padding:0;font-size:16px;text-rendering:optimizeLegibility}body{margin:0;padding:0;background:#f2f2f2;color:#0f0e0e;font-family:Esteban,Georgia,"Times New Roman",serif;font-weight:400;line-height:1.8;font-style:normal;font-size:1.375rem;letter-spacing:0}h1,h2,h3,h4,h5,h6{margin:0 0 .625rem 0;padding:0;font-family:Montserrat,Helvetica,Arial,sans-serif;font-size:1.5rem;font-weight:700;color:#0f0e0e;letter-spacing:-1px}p{margin:0 0 10px 0}a{color:#551114;text-decoration:none}a:active{color:#551114}a:hover{color:#cd282f}ul{margin:0;padding:0}ul li{list-style:none;list-style-position:inside;margin:0;padding:0}b,strong{font-weight:700}button{margin:0;padding:0;outline:0;border:none}.footer-main,.header-main,.home-navigation,.main,.page-banner,.summary,.video-container,.wrapper{position:relative;overflow:hidden;display:block}.wrapper{width:calc(100% - 40px);margin:0 auto}@media (min-width:1140px){.wrapper{width:1140px}}.main{padding-top:20px;background-color:#f2f2f2}.gradient-banner{position:absolute;top:0;left:0;width:100%;height:100%;z-index:100;background:url(img/gradient-header.png) 0 0 repeat-x;display:none;pointer-events:none}@media (min-width:1024px){.gradient-banner{display:block}}.page-banner{width:100%;height:350px;z-index:1;margin-top:75px;background-color:#252525}.page-banner>img{position:absolute;top:0;left:0;object-fit:cover;object-position:center;width:100%;height:100%}@media (min-width:1024px){.page-banner{margin-top:0}}@media (min-width:768px){#home .page-banner{display:none}}.btn{display:inline-block;height:44px;padding:0 32px;border-radius:2px;font-family:Montserrat,Helvetica,Arial,sans-serif;font-weight:700;font-size:.777rem;line-height:44px;text-align:center;text-transform:uppercase;color:#fff;background-color:#cd282f;border-bottom:3px solid #551114}.btn:hover{background-color:#131313;border-bottom:3px solid #000}.btn.mobile-btn{height:44px;line-height:44px;font-size:.777rem;line-height:44px}.header-main{position:fixed;top:0;left:0;width:100%;height:75px;background-color:#0f0e0e;z-index:1000;-webkit-transition:all .3s ease-in-out;-moz-transition:all .3s ease-in-out;-ms-transition:all .3s ease-in-out;-o-transition:all .3s ease-in-out;transition:all .3s ease-in-out}.header-main .wrapper{display:flex;flex-direction:row;justify-content:space-between;align-items:flex-start;height:75px}@media (min-width:768px){.header-main{height:95px}.header-main .wrapper{height:95px}}@media (min-width:1024px){.header-main{background-color:transparent}.header-main .wrapper{padding-top:15px}.header-main.reveal{background-color:#0f0e0e}}.logo{align-self:center;font-family:Montserrat,Helvetica,Arial,sans-serif;font-size:2rem;color:#fff;text-shadow:2px 4px 3px rgba(0,0,0,.1)}@media (min-width:768px){.logo{font-size:3.5rem}}@media (min-width:1024px){.logo{top:-10px}}.logo:hover{color:#fff}.menu-btn{display:block;position:fixed;top:0;right:0;left:auto;width:75px;height:75px;background:0 0;z-index:1001}@media (min-width:768px){.menu-btn{width:95px;height:95px}}.menu-btn .menu-icon{display:block;margin:0 auto;width:32px;height:28px}.menu-btn .menu-icon span{display:block;width:100%;height:3px;position:absolute;right:0;background-color:#fff;-moz-transform:rotate(0);-o-transform:rotate(0);-ms-transform:rotate(0);-webkit-transform:rotate(0);transform:rotate(0);moz-transform-origin:left center;-o-transform-origin:left center;-ms-transform-origin:left center;-webkit-transform-origin:left center;transform-origin:left center;-webkit-transition:all .3s ease-in-out;-moz-transition:all .3s ease-in-out;-ms-transition:all .3s ease-in-out;-o-transition:all .3s ease-in-out;transition:all .3s ease-in-out}.menu-btn .menu-icon span:nth-of-type(1){top:0}.menu-btn .menu-icon span:nth-of-type(2){top:50%;right:0;-moz-transform:translate(0,-50%);-o-transform:translate(0,-50%);-ms-transform:translate(0,-50%);-webkit-transform:translate(0,-50%);transform:translate(0,-50%)}.menu-btn .menu-icon span:nth-of-type(3){bottom:0}.menu-btn.open .menu-icon span:nth-of-type(1){top:2px;left:3px;-moz-transform:rotate(45deg);-o-transform:rotate(45deg);-ms-transform:rotate(45deg);-webkit-transform:rotate(45deg);transform:rotate(45deg)}.menu-btn.open .menu-icon span:nth-of-type(2){width:0;opacity:0}.menu-btn.open .menu-icon span:nth-of-type(3){width:100%!important;bottom:0;left:3px;-moz-transform:rotate(-45deg);-o-transform:rotate(-45deg);-ms-transform:rotate(-45deg);-webkit-transform:rotate(-45deg);transform:rotate(-45deg)}@media (min-width:1024px){.menu-btn{display:none}}.main-menu{display:none}@media (min-width:1024px){.main-menu{display:flex;align-items:center;width:100%}.main-menu li{display:inline-block;padding:10px 0;width:auto}.main-menu li:before{color:rgba(255,255,255,.7);content:'|';display:inline-block;margin:0 12px;position:relative;top:-1px}.main-menu li:first-child:before,.main-menu li:last-child:before{display:none}.main-menu li a:not(.btn){border-bottom:3px solid transparent;color:#fff;display:inline-block;padding:5px 0;font-family:Montserrat,Helvetica,Arial,sans-serif;font-size:.888888rem;text-transform:uppercase;text-shadow:2px 4px 3px rgba(0,0,0,.1);-webkit-transition:all .3s ease-in-out;-moz-transition:all .3s ease-in-out;-ms-transition:all .3s ease-in-out;-o-transition:all .3s ease-in-out;transition:all .3s ease-in-out}.main-menu li a:not(.btn):hover{border-color:#fff}.main-menu li a.btn{height:55px;line-height:55px;font-size:.888888rem;margin:-4px 0 0 24px;box-shadow:2px 4px 3px rgba(0,0,0,.1)}.main-menu li a.btn:hover{background-color:#fff;border-color:#787878;color:#333}}#home .main-menu .menu-item-home,#home .main-menu li:nth-of-type(2):before{display:none}a.btn.btn-registar{display:none}@media (min-width:1024px){a.btn.btn-registar{display:block}}